function init(){resize(),hour(),setInterval(hour,1e3),$('[data-toggle="tooltip"]').tooltip(),$("[data-countdown]").each(function(){var t=$(this),i=$(this).data("countdown");$(t).countdown(i,function(i){var n=i.strftime("%-D"),e=i.strftime("%H");if(0!=n)var n="24";var e=parseInt(n)+parseInt(e);if("10">e)var e="0"+e;$(t).html(i.strftime(e+":%M:%S"))})})}function resize(){fullHeight("aside","min"),fullHeight("#login","min")}function fullHeight(t,i){var n=$(window).height();i?$(t).css("min-height",n):$(t).css("height",n)}function hour(){var t=moment().format("H:mm:ss");$(".sideTimer").html(t)}$(function(){init(),$(window).resize(function(){resize()})});
